mapper.select:
SELECT p.projectNo, p.brandNo, brandName, p.categoryNo, categoryName, projectName, projectImageUrl, projectGoalAmount, projectAttainAmount, projectEndDate, statusNo, FLOOR(projectEndDate - SYSDATE) remainDays, NVL(totalAmount,0) totalAmount, NVL(totalAmount,0)/projectGoalAmount attainRate FROM project p LEFT OUTER JOIN( SELECT projectNo, sum(totalAmount) totalAmount FROM reward_buy_overview GROUP BY projectNo ) ro ON ro.projectNo = p.projectNo JOIN brand b ON p.brandNo = b.brandNo JOIN project_category pc on p.categoryNo = pc.categoryNo WHERE statusNo=5
AND p.categoryNo = #{categoryNo}
ORDER BY projectNo DESC
